mapper.select:
SELECT t.id subTaskId, s.source_db sourceTableSchema, s.source_tbl sourceTableName, s.target_db targetTableSchema, s.target_tbl targetTableName, s.is_add_target_tbl isAddTargetTableFlag, s.ctime beginTime, s.mtime updateTime, IF ( (s.add_target_tbl_status = 2 OR s.add_field_status = 2 OR s.add_json_status = 2 OR s.target_mapping_status = 2), 2, IF (( s.add_target_tbl_status = 1 AND s.add_field_status = 1 AND s.add_json_status = 1 AND target_mapping_status =1), 1, 0 ) ) resultStatus, IFNULL( s.add_target_tbl_reason, IFNULL( s.add_field_reason, IFNULL(s.add_json_reason,s.target_mapping_reason) ) ) failReason FROM t_sub_task_info s LEFT JOIN t_task_info t ON s.task_id = t.id
and t.uid = #{userId,jdbcType=VARCHAR}
AND t.id LIKE #{dto.subTaskId,jdbcType=VARCHAR}
AND (s.source_db LIKE #{dto.tableSchema,jdbcType=VARCHAR} OR s.target_db LIKE #{dto.tableSchema,jdbcType=VARCHAR})
AND (s.source_tbl LIKE #{dto.tableName,jdbcType=VARCHAR} OR s.target_tbl LIKE #{dto.tableName,jdbcType=VARCHAR} )
AND (s.add_target_tbl_status = 0 OR s.add_field_status = 0 OR s.add_json_status = 0) AND (s.add_target_tbl_status != 2 AND s.add_field_status != 2 AND s.add_json_status != 2)
AND s.add_target_tbl_status = 1 AND s.add_field_status = 1 AND s.add_json_status = 1
AND (s.add_target_tbl_status = 2 OR s.add_field_status = 2 OR s.add_json_status = 2)
AND s.ctime >= #{dto.startTime}
AND s.mtime <= #{dto.endTime}
ORDER BY s.ctime DESC
